// Custom JS
$(function(){$(".toggle-hide").click(function(e){e.preventDefault();$(this).toggleClass("opened");$("li.content").toggle("slide",{direction:"right"},100)});$(".toggle-box").click(function(e){e.preventDefault();var t=$(this);$(".toggle-box").not(t).prev().slideUp("fast");$(".toggle-box").not(t).next().slideUp("fast");$(".toggle-box").not(t).parent().find(".company-logo").removeClass("top-pos",200);t.prev().slideToggle("fast");t.next().slideToggle("fast");t.parent().find(".company-logo").toggleClass("top-pos",200)});$(".scroll").click(function(e){e.preventDefault();var t=0;$(this.hash).offset().top>$(document).height()-$(window).height()?t=$(document).height()-$(window).height():t=$(this.hash).offset().top;$("html,body").animate({scrollTop:t},1e3,"swing")})});